function createTextFilter(containerId, fieldName, optionsHTML) {

    var div = document.createElement('div');
    div.id = "div_" + fieldName;
    div.className = "filterselect";
    document.getElementById(containerId).appendChild(div);

    // INPUT
    var input = document.createElement('input');
    input.id = "input_" + fieldName;
    input.placeholder = "Type to filter...";
    input.setAttribute("list", "list_" + fieldName);
    input.oninput = filterFunc;
    div.appendChild(input);

    // DATALIST (REUSE EXISTING OPTION STRING)
    var datalist = document.createElement('datalist');
    datalist.id = "list_" + fieldName;
    datalist.innerHTML = optionsHTML;
    div.appendChild(datalist);

    // LABEL
    var label = document.createElement('div');
    label.innerHTML = fieldName;
    label.className = "filterlabel";
    div.appendChild(label);

    // CLEAR FILTER
    var reset = document.createElement('div');
    reset.innerHTML = "clear filter";
    reset.className = "filterlabel";
    reset.onclick = function () {
        input.value = "";
        filterFunc();
    };
    div.appendChild(reset);
}

